import React, { useState } from 'react';

const Settings = ({ onCustomSet }) => {
  const [customWork, setCustomWork] = useState(25);
  const [customBreak, setCustomBreak] = useState(5);
  const [error, setError] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    // Validate inputs
    if (customWork < 0 || customBreak < 0) {
      setError('Please enter non-negative values.');
      return;
    }
    
    // Reset error if inputs are valid
    setError('');
    onCustomSet(customWork, customBreak); // Call the passed function with validated values
  };

  return (
    <div>
      <h2>Settings</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>} {/* Show error if any */}
      <form onSubmit={handleSubmit}>
        <div>
          <label>Custom Work Minutes:</label>
          <input
            type="number"
            value={customWork}
            onChange={(e) => setCustomWork(Number(e.target.value))}
            min="0" // Prevent negative input
            required
          />
        </div>
        <div>
          <label>Custom Break Minutes:</label>
          <input
            type="number"
            value={customBreak}
            onChange={(e) => setCustomBreak(Number(e.target.value))}
            min="0" // Prevent negative input
            required
          />
        </div>
        <button type="submit">Set Custom Timer</button>
      </form>
    </div>
  );
};

export default Settings;
