//LoginPage.js

import React, { useState } from 'react';
import { loginUser } from '../services/authServices'; // Adjust path if needed
import { useNavigate } from 'react-router-dom'; // If using React Router for navigation


const LoginPage = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(null);
  const navigate = useNavigate(); // For redirecting after successful login

  const handleSubmit = async (e) => {
    e.preventDefault(); // Prevent page reload
    try {
      const userData = await loginUser(email, password); // Call login service
      // On successful login, you can navigate to the dashboard or another page
      navigate('/dashboard'); // Change to the appropriate page in your app
    } catch (err) {
      setError('Invalid email or password'); // Display error message if login fails
    }
  };

  return (
    <div className="login-container">
      <h2>Login</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <form onSubmit={handleSubmit}>
        <div>
          <label>Email:</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Password:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit">Login</button>
        <button type="submit">SignUp</button>
      </form>
    </div>
  );
};

export default LoginPage;
