import React, { useState } from "react";
import Timer from "../components/Timer";
import Control from "../components/Control";
import Settings from "../components/Settings";

const Dashboard = () => {
  const [workMinutes, setWorkMinutes] = useState(25);
  const [breakMinutes, setBreakMinutes] = useState(5);
  const [isActive, setIsActive] = useState(false);
  const [isWorkMode, setIsWorkMode] = useState(true);
  const [minutes, setMinutes] = useState(workMinutes); // Add state for minutes
  const [seconds, setSeconds] = useState(0); // Add state for seconds

  const handleStart = () => {
    setIsActive(true);
    setIsWorkMode(true); // Start in work mode
  };

  const handlePause = () => setIsActive(false);

  const handleReset = () => {
    setIsActive(false);
    setIsWorkMode(true); // Reset to work mode
    setMinutes(workMinutes); // Reset minutes to initial work minutes
    setSeconds(0); // Reset seconds to 0
  };

  const handleCustomSet = (customWork, customBreak) => {
    setWorkMinutes(customWork);
    setBreakMinutes(customBreak);
    setIsActive(false); // Stop the timer when updating custom times
  };

  return (
    <div>
      <h1>Pomodoro Timer Dashboard</h1>
      <Timer
        isActive={isActive}
        isWorkMode={isWorkMode}
        workMinutes={minutes} // Pass the minutes state to Timer
        breakMinutes={breakMinutes}
        setMinutes={setMinutes} // Pass setMinutes to Timer if needed
        setSeconds={setSeconds} // Pass setSeconds to Timer if needed
      />
      <Control onStart={handleStart} onPause={handlePause} onReset={handleReset} />
      <Settings onCustomSet={handleCustomSet} />
    </div>
  );
};

export default Dashboard;
