// Timer.js
import React, { useState, useEffect } from "react";

const Timer = ({ isActive, isWorkMode, workMinutes, breakMinutes, minutes, seconds, setMinutes, setSeconds }) => {
  useEffect(() => {
    let countdown;
    if (isActive) {
      countdown = setInterval(() => {
        if (seconds > 0) {
          setLocalSeconds(seconds - 1);
        } else if (minutes > 0) {
          setMinutes(minutes - 1);
          setLocalSeconds(59);
        } else {
          // Switch between work and break
          setIsWorkMode((prevMode) => !prevMode); // Toggle work/break mode
          if (isWorkMode) {
            setMinutes(breakMinutes);
          } else {
            setMinutes(workMinutes);
          }
          setSeconds(0); // Reset seconds when switching
        }
      }, 1000);
    }
    return () => clearInterval(countdown);
  }, [isActive, seconds, minutes, isWorkMode, setMinutes, workMinutes, breakMinutes, setIsWorkMode]);
  
  return (
    <h1>
      {isWorkMode ? "Work Time" : "Break Time"}: {minutes}:{seconds < 10 ? `0${seconds}` : seconds}
    </h1>
  );
};


export default Timer;
