import React, { useState, useEffect } from "react";

const Timer = ({ isActive, isWorkMode, workMinutes, breakMinutes, setMinutes, setSeconds }) => {
  useEffect(() => {
    // Set the initial values based on workMinutes
    setMinutes(workMinutes);
    setSeconds(0);
  }, [workMinutes, setMinutes, setSeconds]);

  useEffect(() => {
    if (isActive) {
      if (workMinutes === 0 && breakMinutes === 0) {
        // Switch between work and break
        if (isWorkMode) {
          setMinutes(breakMinutes);
          setSeconds(0);
        } else {
          setMinutes(workMinutes);
          setSeconds(0);
        }
      } else {
        const countdown = setInterval(() => {
          if (seconds > 0) {
            setSeconds(seconds - 1);
          } else if (workMinutes > 0) {
            setMinutes(workMinutes - 1);
            setSeconds(59);
          }
        }, 1000);
        return () => clearInterval(countdown);
      }
    }
  }, [isActive, workMinutes, seconds, isWorkMode, setMinutes, setSeconds, breakMinutes]);

  return (
    <h1>
      {isWorkMode ? "Work Time" : "Break Time"}: {workMinutes}:{seconds < 10 ? `0${seconds}` : seconds}
    </h1>
  );
};

export default Timer;
