// Timer.js
import React, { useState, useEffect } from "react";

const Timer = ({ isActive, isWorkMode, workMinutes, breakMinutes, setMinutes, setSeconds }) => {
  const [seconds, setLocalSeconds] = useState(0); // Local seconds state

  useEffect(() => {
    // Initialize timer
    setMinutes(isWorkMode ? workMinutes : breakMinutes);
    setLocalSeconds(0);
  }, [workMinutes, breakMinutes, isWorkMode, setMinutes]);

  useEffect(() => {
    let countdown;
    if (isActive) {
      countdown = setInterval(() => {
        if (seconds > 0) {
          setLocalSeconds(seconds - 1);
        } else if (minutes > 0) {
          setMinutes(minutes - 1);
          setLocalSeconds(59);
        } else {
          // Switch between work and break
          if (isWorkMode) {
            setMinutes(breakMinutes);
          } else {
            setMinutes(workMinutes);
          }
          setLocalSeconds(0); // Reset seconds when switching
          setIsWorkMode(!isWorkMode); // Switch mode
        }
      }, 1000);
    }
    return () => clearInterval(countdown);
  }, [isActive, seconds, minutes, isWorkMode, setMinutes, workMinutes, breakMinutes]);

  return (
    <h1>
      {isWorkMode ? "Work Time" : "Break Time"}: {minutes}:{localSeconds < 10 ? `0${localSeconds}` : localSeconds}
    </h1>
  );
};

export default Timer;
